            {
                data.map((item) => (
                    <h4 key={item.id}> 
                        <Link to={`/blog/${item.id}`}>
                            {item.id} - {item.title}
                        </Link> 
                    </h4>
                ))
            }
        </div>
    )
}

export default Blog
