import React, { useState } from "react";
import { Outlet } from "react-router";
import Header from "./components/Header";
import Footer from "./components/Footer";

import styled, { ThemeProvider } from "styled-components";
import { lightTheme, darkTheme, darkRed, darkBlue, GlobalStyles } from "./assets/css/themes";

const StyledApp = styled.div`
  color: ${(props) => props.theme.fontColor};
`;

const App = () => {

  const [theme, setTheme] = useState("light");

  const themeToggler = () => {
    theme === "light" ? setTheme("dark") : setTheme("light");
  };

  return (
    <>
      <Header />
      <div className="container">
        <div class="row">
          <div
            className="col-12"
            style={{ margin: "0px auto 0 auto", width: "1024px" }}
          >
            <Outlet />
          </div>
        </div>
      </div>
      <div className="temas">
      <ThemeProvider theme={theme === "light" ? lightTheme : darkTheme}>
          <GlobalStyles />
          <StyledApp className="selector-theme1">
            <button onClick={() => themeToggler()}>negro</button>
          </StyledApp>
        </ThemeProvider>
        <ThemeProvider theme={theme === "darkRed" ? lightTheme : darkRed}>
          <GlobalStyles />
          <StyledApp className="selector-theme2">
            <button onClick={() => themeToggler()}>rojo</button>
          </StyledApp>
        </ThemeProvider>
        <ThemeProvider theme={theme === "darkBlue" ? lightTheme : darkBlue}>
          <GlobalStyles />
          <StyledApp className="selector-theme3">
            <button onClick={() => themeToggler()}>azul</button>
          </StyledApp>
        </ThemeProvider>
      </div>
      <Footer />
    </>
  );
};

export default App;
